#include "PCFG.h"
#include <chrono>
#include <fstream>
#include "md5.h"
#include <iomanip>
using namespace std;
using namespace chrono;

// 编译指令如下：
// g++ correctness.cpp train.cpp guessing.cpp md5.cpp -o test.exe


// 通过这个函数，你可以验证你实现的SIMD哈希函数的正确性
int main()
{
    cout << "SIMD*0:" << endl;
    bit32 state[4];
    MD5Hash("", state);
    for (int i1 = 0; i1 < 4; i1 += 1)
    {
        cout << std::setw(8) << std::setfill('0') << hex << state[i1];
    }
    cout << endl;

    cout << "SIMD*2:" << endl;
    bit32 state_2[4 * 4];
    string inputs_2[2] = {"",
                          ""};
    SIMDMD5Hash_2(inputs_2, state_2);
    for (int i1 = 0; i1 < 2; i1 += 1)
    {
        for(int i2 = 0; i2 < 4; i2 += 1){
            cout << std::setw(8) << std::setfill('0') << hex << state_2[i1*4 + i2];
        }
        cout << endl;
    }

    cout << "SIMD*4:" << endl;
    bit32 state_4[4 * 4];
    string inputs_4[4] = {"",
                          "",
                          "",
                          ""};
    SIMDMD5Hash_4(inputs_4, state_4);
    for (int i1 = 0; i1 < 4; i1 += 1)
    {
        for(int i2 = 0; i2 < 4; i2 += 1){
            cout << std::setw(8) << std::setfill('0') << hex << state_4[i1*4 + i2];
        }
        cout << endl;
    }
    
    cout << "SIMD*8 (-):" << endl;
    bit32 state_8b[4 * 8];
    string inputs_8[8] = {"",
                          "",
                          "",
                          "",
                          "",
                          "",
                          "",
                          ""};
    SIMDMD5Hash_8basic(inputs_8, state_8b);
    for (int i1 = 0; i1 < 8; i1 += 1)
    {
        for(int i2 = 0; i2 < 4; i2 += 1){
            cout << std::setw(8) << std::setfill('0') << hex << state_8b[i1*4 + i2];
        }
        cout << endl;
    }

    cout << "SIMD*8 (+):" << endl;
    bit32 state_8a[4 * 8];
    SIMDMD5Hash_8advanced(inputs_8, state_8a);
    for (int i1 = 0; i1 < 8; i1 += 1)
    {
        for(int i2 = 0; i2 < 4; i2 += 1){
            cout << std::setw(8) << std::setfill('0') << hex << state_8b[i1*4 + i2];
        }
        cout << endl;
    }
}